mapper.insert:
    insert into comment_table (id, post_id, user_id, 
      reply_content, time, 
comment_superiors
      )
    values (#{id,jdbcType=BIGINT}, #{postId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, 
      #{replyContent,jdbcType=VARCHAR}, #{time,jdbcType=TIMESTAMP}, #{
commentSuperiors,jdbcType=VARCHAR}
      )

mapper.update:
    update comment_table
    set post_id = #{postId,jdbcType=BIGINT},
      user_id = #{userId,jdbcType=BIGINT},
      reply_content = #{replyContent,jdbcType=VARCHAR},
      time = #{time,jdbcType=TIMESTAMP},

comment_superiors = #{
commentSuperiors,jdbcType=VARCHAR}
    where id = #{id,jdbcType=BIGINT}
